import React, { useState, useEffect } from 'react';
import { useWeb3 } from '../context/Web3Context';
import '../styles/TradingForm.css';

const TradingForm = () => {
    const { web3, contracts, account, ethPrice, refreshData } = useWeb3();
    const [amount, setAmount] = useState('');
    const [leverage, setLeverage] = useState('2');
    const [positionType, setPositionType] = useState('long');
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(null);
    const [success, setSuccess] = useState(null);
    const [estimatedLiquidationPrice, setEstimatedLiquidationPrice] = useState(null);

    // Calculate estimated liquidation price based on inputs
    useEffect(() => {
        if (amount && leverage && ethPrice) {
            try {
                const currentPrice = parseFloat(ethPrice);
                const leverageVal = parseFloat(leverage);
                let liquidationPrice;

                if (positionType === 'long') {
                    // For long positions: liquidation price = entry price * (1 - 1/leverage)
                    liquidationPrice = currentPrice * (1 - 1/leverageVal);
                } else {
                    // For short positions: liquidation price = entry price * (1 + 1/leverage)
                    liquidationPrice = currentPrice * (1 + 1/leverageVal);
                }

                setEstimatedLiquidationPrice(liquidationPrice.toFixed(2));
            } catch (err) {
                console.error("Error calculating liquidation price:", err);
                setEstimatedLiquidationPrice(null);
            }
        } else {
            setEstimatedLiquidationPrice(null);
        }
    }, [amount, leverage, positionType, ethPrice]);

    const handleSubmit = async (e) => {
        e.preventDefault();
        if (!amount || !leverage) {
            setError('Please fill in all fields');
            return;
        }

        try {
            setLoading(true);
            setError(null);
            setSuccess(null);

            if (!web3 || !contracts || !contracts.marginTrading) {
                throw new Error("Web3 or contracts not initialized. Please refresh the page and try again.");
            }

            const amountWei = web3.utils.toWei(amount, 'ether');
            const leverageValue = parseInt(leverage);

            if (positionType === 'long') {
                await contracts.marginTrading.methods
                    .openLongPosition(amountWei, leverageValue)
                    .send({ from: account });
            } else {
                await contracts.marginTrading.methods
                    .openShortPosition(amountWei, leverageValue)
                    .send({ from: account });
            }

            // Reset form
            setAmount('');
            setLeverage('2');
            setSuccess(`Successfully opened a ${positionType} position with ${leverage}x leverage`);

            // Refresh data
            refreshData();

        } catch (error) {
            console.error("Error opening position:", error);
            setError(error.message || "Failed to open position. Please try again.");
        } finally {
            setLoading(false);
        }
    };

    const handleClosePosition = async () => {
        try {
            setLoading(true);
            setError(null);
            setSuccess(null);

            if (!web3 || !contracts || !contracts.marginTrading) {
                throw new Error("Web3 or contracts not initialized. Please refresh the page and try again.");
            }

            await contracts.marginTrading.methods
                .closePosition()
                .send({ from: account });

            setSuccess("Position closed successfully");

            // Refresh data
            refreshData();

        } catch (error) {
            console.error("Error closing position:", error);
            setError(error.message || "Failed to close position. Please try again.");
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="trading-panel">
            <div className="trading-form-container">
                <h2>Open Position</h2>
                <div className="current-price">
                    <span>Current ETH Price: </span>
                    <span className="price-value">${parseFloat(ethPrice).toFixed(2)}</span>
                </div>

                <form onSubmit={handleSubmit} className="trading-form">
                    <div className="form-group">
                        <label htmlFor="amount">Amount (ETH)</label>
                        <input
                            type="number"
                            id="amount"
                            value={amount}
                            onChange={(e) => setAmount(e.target.value)}
                            step="0.01"
                            min="0.01"
                            placeholder="0.1"
                            className="form-control"
                        />
                    </div>

                    <div className="form-group">
                        <label htmlFor="leverage">Leverage</label>
                        <select
                            id="leverage"
                            value={leverage}
                            onChange={(e) => setLeverage(e.target.value)}
                            className="form-control"
                        >
                            <option value="2">2x</option>
                            <option value="3">3x</option>
                            <option value="5">5x</option>
                            <option value="10">10x</option>
                            <option value="20">20x</option>
                        </select>
                    </div>

                    <div className="form-group">
                        <label htmlFor="positionType">Position Type</label>
                        <div className="position-type-selector">
                            <button 
                                type="button" 
                                className={`position-btn ${positionType === 'long' ? 'long active' : ''}`}
                                onClick={() => setPositionType('long')}
                            >
                                Long
                            </button>
                            <button 
                                type="button" 
                                className={`position-btn ${positionType === 'short' ? 'short active' : ''}`}
                                onClick={() => setPositionType('short')}
                            >
                                Short
                            </button>
                        </div>
                    </div>

                    {estimatedLiquidationPrice && (
                        <div className="liquidation-estimate">
                            <span>Estimated Liquidation Price: </span>
                            <span className="price-value">${estimatedLiquidationPrice}</span>
                        </div>
                    )}

                    {error && <div className="error-message">{error}</div>}
                    {success && <div className="success-message">{success}</div>}

                    <button 
                        type="submit" 
                        disabled={loading || !amount} 
                        className="action-button open-button"
                    >
                        {loading ? 'Processing...' : 'Open Position'}
                    </button>
                </form>

                <div className="close-position-container">
                    <h2>Close Position</h2>
                    <button
                        onClick={handleClosePosition}
                        disabled={loading}
                        className="action-button close-button"
                    >
                        {loading ? 'Processing...' : 'Close Position'}
                    </button>
                </div>
            </div>
        </div>
    );
};

export default TradingForm;
