import React, { useState, useEffect } from 'react';
import { useWeb3 } from '../context/Web3Context';

const PositionInfo = () => {
    const { contracts, account } = useWeb3();
    const [position, setPosition] = useState(null);
    const [balance, setBalance] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    const fetchPositionInfo = async () => {
        try {
            setLoading(true);
            setError(null);

            // Get position details
            const positionInfo = await contracts.marginTrading.methods
                .getPosition(account)
                .call();

            // Get ETH balance
            const balanceWei = await window.web3.eth.getBalance(account);
            const balanceEth = window.web3.utils.fromWei(balanceWei, 'ether');

            setPosition({
                exists: positionInfo.exists,
                isLong: positionInfo.isLong,
                amount: window.web3.utils.fromWei(positionInfo.amount, 'ether'),
                leverage: positionInfo.leverage,
                entryPrice: window.web3.utils.fromWei(positionInfo.entryPrice, 'ether'),
                liquidationPrice: window.web3.utils.fromWei(positionInfo.liquidationPrice, 'ether'),
                pnl: window.web3.utils.fromWei(positionInfo.pnl, 'ether'),
                timestamp: new Date(parseInt(positionInfo.timestamp) * 1000).toLocaleString()
            });

            setBalance(balanceEth);

        } catch (error) {
            setError(error.message);
        } finally {
            setLoading(false);
        }
    };

    useEffect(() => {
        fetchPositionInfo();
        // Update every 10 seconds
        const interval = setInterval(fetchPositionInfo, 10000);
        return () => clearInterval(interval);
    }, [account]);

    if (loading) {
        return <div>Loading position information...</div>;
    }

    if (error) {
        return <div className="error">Error: {error}</div>;
    }

    return (
        <div className="position-info">
            <h2>Account Information</h2>
            <div className="info-group">
                <label>ETH Balance:</label>
                <span>{parseFloat(balance).toFixed(4)} ETH</span>
            </div>

            <h2>Current Position</h2>
            {position.exists ? (
                <div className="position-details">
                    <div className="info-group">
                        <label>Position Type:</label>
                        <span className={position.isLong ? 'long' : 'short'}>
                            {position.isLong ? 'Long' : 'Short'}
                        </span>
                    </div>
                    <div className="info-group">
                        <label>Amount:</label>
                        <span>{parseFloat(position.amount).toFixed(4)} ETH</span>
                    </div>
                    <div className="info-group">
                        <label>Leverage:</label>
                        <span>{position.leverage}x</span>
                    </div>
                    <div className="info-group">
                        <label>Entry Price:</label>
                        <span>${parseFloat(position.entryPrice).toFixed(2)}</span>
                    </div>
                    <div className="info-group">
                        <label>Liquidation Price:</label>
                        <span>${parseFloat(position.liquidationPrice).toFixed(2)}</span>
                    </div>
                    <div className="info-group">
                        <label>P&L:</label>
                        <span className={parseFloat(position.pnl) >= 0 ? 'profit' : 'loss'}>
                            {parseFloat(position.pnl).toFixed(4)} ETH
                        </span>
                    </div>
                    <div className="info-group">
                        <label>Opened At:</label>
                        <span>{position.timestamp}</span>
                    </div>
                </div>
            ) : (
                <div className="no-position">
                    No open position
                </div>
            )}
        </div>
    );
};

export default PositionInfo;
