import React, { useState, useEffect } from 'react';
import { useWeb3 } from '../context/Web3Context';
import '../styles/PriceChart.css';

const PriceChart = () => {
    const { ethPrice } = useWeb3();
    const [priceHistory, setPriceHistory] = useState([]);
    const [timeLabels, setTimeLabels] = useState([]);

    const updatePriceData = () => {
        const now = new Date();
        const timeLabel = `${now.getHours()}:${now.getMinutes()}:${now.getSeconds()}`;

        setPriceHistory(prev => {
            const newHistory = [...prev, parseFloat(ethPrice)].slice(-10);
            return newHistory;
        });

        setTimeLabels(prev => {
            const newLabels = [...prev, timeLabel].slice(-10);
            return newLabels;
        });
    };

    useEffect(() => {
        updatePriceData();
    }, [ethPrice]);

    useEffect(() => {
        const interval = setInterval(() => {
            updatePriceData();
        }, 5000);

        return () => clearInterval(interval);
    }, []);

    // Calculate price change percentage (simulated)
    const priceChangePercent = ((Math.random() * 5) - 2.5).toFixed(2);
    const isPriceUp = parseFloat(priceChangePercent) >= 0;

    return (
        <div className="price-chart-container">
            <div className="price-header">
                <h2>ETH/USD Price</h2>
                <div className="current-price-display">
                    <span className="price-label">Current:</span>
                    <span className="price-value">${parseFloat(ethPrice).toFixed(2)}</span>
                </div>
            </div>

            <div className="price-visualization">
                <div className="price-bars">
                    {priceHistory.map((price, index) => {
                        const height = Math.max(30, (price / Math.max(...priceHistory)) * 100);
                        return (
                            <div 
                                key={index} 
                                className="price-bar" 
                                style={{ height: `${height}%` }}
                                title={`$${price.toFixed(2)} at ${timeLabels[index] || ''}`}
                            />
                        );
                    })}
                </div>
            </div>

            <div className="price-info">
                <div className="price-stat">
                    <span className="stat-label">24h Change:</span>
                    <span className={`stat-value ${isPriceUp ? 'positive' : 'negative'}`}>
                        {isPriceUp ? '+' : ''}{priceChangePercent}%
                    </span>
                </div>
                <div className="price-stat">
                    <span className="stat-label">24h High:</span>
                    <span className="stat-value">${(parseFloat(ethPrice) * 1.05).toFixed(2)}</span>
                </div>
                <div className="price-stat">
                    <span className="stat-label">24h Low:</span>
                    <span className="stat-value">${(parseFloat(ethPrice) * 0.95).toFixed(2)}</span>
                </div>
            </div>
        </div>
    );
};

export default PriceChart;
