import React, { useState, useEffect } from 'react';
import { useWeb3 } from '../context/Web3Context';
import '../styles/PositionInfo.css';

const PositionInfo = () => {
    const { web3, contracts, account, ethPrice, refreshData } = useWeb3();
    const [position, setPosition] = useState(null);
    const [balance, setBalance] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    const fetchPositionInfo = async () => {
        if (!web3 || !contracts || !contracts.marginTrading || !account) {
            setLoading(false);
            return;
        }

        try {
            setLoading(true);
            setError(null);

            // Get position details
            const positionInfo = await contracts.marginTrading.methods
                .getPosition(account)
                .call();

            // Get ETH balance
            const balanceWei = await web3.eth.getBalance(account);
            const balanceEth = web3.utils.fromWei(balanceWei, 'ether');

            setPosition({
                exists: positionInfo.exists,
                isLong: positionInfo.isLong,
                amount: web3.utils.fromWei(positionInfo.amount, 'ether'),
                leverage: positionInfo.leverage,
                entryPrice: web3.utils.fromWei(positionInfo.entryPrice, 'ether'),
                liquidationPrice: web3.utils.fromWei(positionInfo.liquidationPrice, 'ether'),
                pnl: web3.utils.fromWei(positionInfo.pnl, 'ether'),
                timestamp: new Date(parseInt(positionInfo.timestamp) * 1000).toLocaleString()
            });

            setBalance(balanceEth);

        } catch (error) {
            console.error("Error fetching position:", error);
            setError("Could not fetch position information. Please try again later.");
            setPosition({
                exists: false,
                isLong: false,
                amount: "0",
                leverage: "0",
                entryPrice: "0",
                liquidationPrice: "0",
                pnl: "0",
                timestamp: "-"
            });
        } finally {
            setLoading(false);
        }
    };

    useEffect(() => {
        if (account && web3 && contracts) {
            fetchPositionInfo();
            // Update every 10 seconds
            const interval = setInterval(fetchPositionInfo, 10000);
            return () => clearInterval(interval);
        }
    }, [account, web3, contracts]);

    // Refresh data when ethPrice changes
    useEffect(() => {
        if (account && web3 && contracts) {
            fetchPositionInfo();
        }
    }, [ethPrice]);

    const handleManualRefresh = () => {
        fetchPositionInfo();
        refreshData();
    };

    if (loading && !position) {
        return <div className="loading-container">Loading position information...</div>;
    }

    return (
        <div className="position-info-container">
            <div className="position-info-header">
                <h2>Account Information</h2>
                <button onClick={handleManualRefresh} className="refresh-button">
                    <span>â†»</span> Refresh
                </button>
            </div>

            {error && <div className="error-message">{error}</div>}

            <div className="info-card">
                <div className="info-group">
                    <label>ETH Balance:</label>
                    <span className="value">{balance ? parseFloat(balance).toFixed(4) : '0.0000'} ETH</span>
                </div>

                <div className="info-group">
                    <label>Current ETH Price:</label>
                    <span className="value">${parseFloat(ethPrice).toFixed(2)}</span>
                </div>
            </div>

            <h2>Current Position</h2>
            {position && position.exists ? (
                <div className="position-details">
                    <div className="position-header">
                        <div className={`position-badge ${position.isLong ? 'long' : 'short'}`}>
                            {position.isLong ? 'LONG' : 'SHORT'}
                        </div>
                        <div className="position-leverage">{position.leverage}x</div>
                    </div>

                    <div className="position-grid">
                        <div className="info-group">
                            <label>Amount:</label>
                            <span className="value">{parseFloat(position.amount).toFixed(4)} ETH</span>
                        </div>

                        <div className="info-group">
                            <label>Entry Price:</label>
                            <span className="value">${parseFloat(position.entryPrice).toFixed(2)}</span>
                        </div>

                        <div className="info-group">
                            <label>Liquidation Price:</label>
                            <span className="value">${parseFloat(position.liquidationPrice).toFixed(2)}</span>
                        </div>

                        <div className="info-group">
                            <label>P&L:</label>
                            <span className={`value ${parseFloat(position.pnl) >= 0 ? 'profit' : 'loss'}`}>
                                {parseFloat(position.pnl).toFixed(4)} ETH
                            </span>
                        </div>

                        <div className="info-group">
                            <label>Opened At:</label>
                            <span className="value">{position.timestamp}</span>
                        </div>
                    </div>
                </div>
            ) : (
                <div className="no-position">
                    <p>No open position</p>
                    <p className="no-position-hint">Open a position using the form on the left</p>
                </div>
            )}
        </div>
    );
};

export default PositionInfo;
