import React, { useState, useEffect } from 'react';
import { useWeb3 } from '../context/Web3Context';
import '../styles/PositionInfo.css';

const PositionInfo = () => {
    const { web3, contracts, account, ethPrice, refreshData } = useWeb3();
    const [positions, setPositions] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        if (web3 && contracts && account) {
            fetchPositions();
        }
    }, [web3, contracts, account, ethPrice]);

    const fetchPositions = async () => {
        try {
            setLoading(true);
            setError(null);

            // Get the number of positions for the user
            const positionCount = await contracts.marginTrading.methods
                .userPositionCount(account)
                .call();

            const positionsData = [];

            // Fetch each position
            for (let i = 0; i < positionCount; i++) {
                const position = await contracts.marginTrading.methods
                    .positions(account, i)
                    .call();

                // Format position data
                const formattedPosition = {
                    id: i,
                    open: position.open,
                    margin: web3.utils.fromWei(position.margin, 'ether'),
                    positionSize: web3.utils.fromWei(position.positionSize, 'ether'),
                    entryPrice: web3.utils.fromWei(position.entryPrice, 'ether'),
                    leverage: position.leverage,
                    positionType: position.positionType === '0' ? 'Long' : 'Short',
                    pnl: '0', // Will calculate below if position is open
                    collateralToken: position.collateralToken === '0x0000000000000000000000000000000000000000' ? 'ETH' : 'Token'
                };

                // Calculate PnL if position is open
                if (position.open) {
                    const currentPrice = parseFloat(ethPrice);
                    const entryPrice = parseFloat(formattedPosition.entryPrice);
                    const positionSize = parseFloat(formattedPosition.positionSize);

                    let pnl = 0;
                    if (formattedPosition.positionType === 'Long') {
                        pnl = positionSize * (currentPrice - entryPrice);
                    } else {
                        pnl = positionSize * (entryPrice - currentPrice);
                    }

                    formattedPosition.pnl = pnl.toFixed(4);
                }

                positionsData.push(formattedPosition);
            }

            setPositions(positionsData);
        } catch (error) {
            console.error("Error fetching positions:", error);
            setError("Failed to load position data. Please refresh the page.");
        } finally {
            setLoading(false);
        }
    };

    const handleUpdatePosition = async (positionId) => {
        try {
            setLoading(true);
            await contracts.marginTrading.methods
                .updatePosition(positionId)
                .send({ from: account });

            // Refresh data
            await refreshData();
            await fetchPositions();
        } catch (error) {
            console.error("Error updating position:", error);
            setError("Failed to update position. Please try again.");
        } finally {
            setLoading(false);
        }
    };

    if (loading && positions.length === 0) {
        return (
            <div className="position-info-container">
                <h2>Your Positions</h2>
                <div className="loading-spinner"></div>
            </div>
        );
    }

    if (error) {
        return (
            <div className="position-info-container">
                <h2>Your Positions</h2>
                <div className="error-message">{error}</div>
            </div>
        );
    }

    return (
        <div className="position-info-container">
            <h2>Your Positions</h2>

            {positions.length === 0 ? (
                <div className="no-positions">
                    <p>You don't have any positions yet.</p>
                </div>
            ) : (
                <div className="positions-list">
                    {positions.map(position => (
                        <div key={position.id} className={`position-card ${position.open ? 'open' : 'closed'}`}>
                            <div className="position-header">
                                <span className={`position-type ${position.positionType.toLowerCase()}`}>
                                    {position.positionType}
                                </span>
                                <span className={`position-status ${position.open ? 'open' : 'closed'}`}>
                                    {position.open ? 'Open' : 'Closed'}
                                </span>
                            </div>

                            <div className="position-details">
                                <div className="detail-row">
                                    <span className="detail-label">Position ID:</span>
                                    <span className="detail-value">{position.id}</span>
                                </div>
                                <div className="detail-row">
                                    <span className="detail-label">Collateral:</span>
                                    <span className="detail-value">{position.collateralToken}</span>
                                </div>
                                <div className="detail-row">
                                    <span className="detail-label">Margin:</span>
                                    <span className="detail-value">{position.margin}</span>
                                </div>
                                <div className="detail-row">
                                    <span className="detail-label">Size:</span>
                                    <span className="detail-value">{position.positionSize} ETH</span>
                                </div>
                                <div className="detail-row">
                                    <span className="detail-label">Entry Price:</span>
                                    <span className="detail-value">${parseFloat(position.entryPrice).toFixed(2)}</span>
                                </div>
                                <div className="detail-row">
                                    <span className="detail-label">Current Price:</span>
                                    <span className="detail-value">${parseFloat(ethPrice).toFixed(2)}</span>
                                </div>
                                <div className="detail-row">
                                    <span className="detail-label">Leverage:</span>
                                    <span className="detail-value">{position.leverage}x</span>
                                </div>
                                {position.open && (
                                    <div className="detail-row">
                                        <span className="detail-label">PnL:</span>
                                        <span className={`detail-value ${parseFloat(position.pnl) >= 0 ? 'positive' : 'negative'}`}>
                                            {parseFloat(position.pnl) >= 0 ? '+' : ''}{position.pnl} ETH
                                        </span>
                                    </div>
                                )}
                            </div>

                            {position.open && (
                                <button 
                                    onClick={() => handleUpdatePosition(position.id)}
                                    className="update-position-btn"
                                    disabled={loading}
                                >
                                    {loading ? 'Updating...' : 'Update Position'}
                                </button>
                            )}
                        </div>
                    ))}
                </div>
            )}
        </div>
    );
};

export default PositionInfo;
