import React, { createContext, useState, useEffect, useContext } from 'react';
import Web3 from 'web3';
import MarginTrading from '../contracts/MarginTrading.json';
import MockToken from '../contracts/MockToken.json';
import MockPriceFeed from '../contracts/MockPriceFeed.json';

export const Web3Context = createContext();

export const useWeb3 = () => {
    const context = useContext(Web3Context);
    if (!context) {
        throw new Error('useWeb3 must be used within a Web3Provider');
    }
    return context;
};

export const Web3Provider = ({ children }) => {
    const [web3, setWeb3] = useState(null);
    const [contracts, setContracts] = useState(null);
    const [account, setAccount] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const initWeb3 = async () => {
            try {
                // Check if MetaMask is installed
                if (window.ethereum) {
                    const web3Instance = new Web3(window.ethereum);
                    setWeb3(web3Instance);

                    // Request account access
                    const accounts = await window.ethereum.request({ method: 'eth_requestAccounts' });
                    setAccount(accounts[0]);

                    // Initialize contracts
                    const networkId = await web3Instance.eth.net.getId();

                    const marginTradingContract = new web3Instance.eth.Contract(
                        MarginTrading.abi,
                        MarginTrading.networks[networkId]?.address
                    );

                    const mockTokenContract = new web3Instance.eth.Contract(
                        MockToken.abi,
                        MockToken.networks[networkId]?.address
                    );

                    const mockPriceFeedContract = new web3Instance.eth.Contract(
                        MockPriceFeed.abi,
                        MockPriceFeed.networks[networkId]?.address
                    );

                    setContracts({
                        marginTrading: marginTradingContract,
                        mockToken: mockTokenContract,
                        mockPriceFeed: mockPriceFeedContract
                    });

                    // Listen for account changes
                    window.ethereum.on('accountsChanged', (accounts) => {
                        setAccount(accounts[0]);
                    });

                    // Listen for network changes
                    window.ethereum.on('chainChanged', () => {
                        window.location.reload();
                    });
                } else {
                    setError('Please install MetaMask to use this application');
                }
            } catch (error) {
                setError(error.message);
            } finally {
                setLoading(false);
            }
        };

        initWeb3();
    }, []);

    return (
        <Web3Context.Provider value={{ web3, contracts, account, loading, error }}>
            {children}
        </Web3Context.Provider>
    );
};
