import React, { useState, useEffect } from 'react';
import { Web3Provider, useWeb3 } from './context/Web3Context';
import TradingForm from './components/TradingForm';
import PositionInfo from './components/PositionInfo';
import './App.css';

// ConnectWallet component to handle wallet connection
const ConnectWallet = () => {
    const { connectWallet, account, error, networkId } = useWeb3();

    const getNetworkName = (id) => {
        switch(id) {
            case 1: return 'Ethereum Mainnet';
            case 3: return 'Ropsten Testnet';
            case 4: return 'Rinkeby Testnet';
            case 5: return 'Goerli Testnet';
            case 42: return 'Kovan Testnet';
            case 1337: return 'Local Development';
            case 31337: return 'Local Development';
            default: return `Network ID: ${id}`;
        }
    };

    return (
        <div className="connect-wallet">
            {!account ? (
                <>
                    <button onClick={connectWallet} className="connect-button">
                        Connect MetaMask
                    </button>
                    {error && <p className="error-message">{error}</p>}
                </>
            ) : (
                <div className="account-info">
                    <span className="network-badge">{networkId ? getNetworkName(networkId) : 'Unknown Network'}</span>
                    <span className="account-address">Connected: {account.slice(0, 6)}...{account.slice(-4)}</span>
                </div>
            )}
        </div>
    );
};

// Main App content that requires wallet connection
const AppContent = () => {
    const { account, loading, contracts } = useWeb3();
    const [currentTime, setCurrentTime] = useState(new Date());

    useEffect(() => {
        const timer = setInterval(() => {
            setCurrentTime(new Date());
        }, 1000);

        return () => clearInterval(timer);
    }, []);

    if (loading) {
        return <div className="loading-container">
            <div className="loading-spinner"></div>
            <p>Loading web3 connection...</p>
        </div>;
    }

    if (!account) {
        return (
            <div className="connect-container">
                <h2>Please connect your wallet to use the trading platform</h2>
                <p>This DApp allows you to open leveraged long and short positions on ETH</p>
                <ConnectWallet />
            </div>
        );
    }

    if (!contracts) {
        return (
            <div className="error-container">
                <h2>Contract Connection Error</h2>
                <p>Could not connect to the smart contracts. Please make sure you're on the correct network.</p>
            </div>
        );
    }

    return (
        <div className="trading-container">
            <TradingForm />
            <PositionInfo />
        </div>
    );
};

function App() {
    return (
        <Web3Provider>
            <div className="App">
                <header className="App-header">
                    <h1>Margin Trading DApp</h1>
                    <ConnectWallet />
                </header>
                <main className="App-main">
                    <AppContent />
                </main>
                <footer className="App-footer">
                    <p> {new Date().getFullYear()} Margin Trading DApp - For educational purposes only</p>
                </footer>
            </div>
        </Web3Provider>
    );
}

export default App;
