import React, { createContext, useContext, useState, useEffect, useCallback } from 'react';
import Web3 from 'web3';

// Import ABIs - use the correct paths and names
import MarginTradingABI from '../abis/MarginTradeManager.json';
import MockTokenABI from '../abis/MockToken.json';
import LiquidationEngineABI from '../contracts/MockPriceFeed.json'; // Using MockPriceFeed as LiquidationEngine for now

// Create context
const Web3Context = createContext();

// Contract addresses for Sepolia testnet - using the exact addresses provided
const CONTRACT_ADDRESSES = {
    11155111: { // Sepolia
        marginTrading: '0xfD79e1b120f3aE000f2d79aCEca92Aaec0B34C14',
        mockToken: '0x6Bb00934FD4f6db39F475Bf39283981A20eCCe5b',
        liquidationEngine: '0x290346048a11574A6bEb6E0B3B0E353b0bE1cD0D'
    },
    // Add other networks as needed
};

export const Web3Provider = ({ children }) => {
    const [web3, setWeb3] = useState(null);
    const [account, setAccount] = useState(null);
    const [networkId, setNetworkId] = useState(null);
    const [contracts, setContracts] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [ethPrice, setEthPrice] = useState('1500.00'); // Default price

    // Initialize web3
    const initializeWeb3 = useCallback(async () => {
        try {
            let web3Instance;

            // Check if MetaMask is installed
            if (window.ethereum) {
                web3Instance = new Web3(window.ethereum);

                try {
                    // Request account access
                    await window.ethereum.request({ method: 'eth_requestAccounts' });

                    // Get the connected account
                    const accounts = await web3Instance.eth.getAccounts();
                    setAccount(accounts[0]);

                    // Get network ID
                    const networkId = await web3Instance.eth.net.getId();
                    setNetworkId(networkId);

                    setWeb3(web3Instance);

                    // Initialize contracts
                    await initializeContracts(web3Instance, networkId);

                } catch (error) {
                    console.error("User denied account access", error);
                    setError("Please connect your MetaMask wallet to use this application.");
                }
            } else {
                console.error("MetaMask is not installed");
                setError("MetaMask is not installed. Please install MetaMask to use this application.");
            }
        } catch (error) {
            console.error("Error initializing web3", error);
            setError("Failed to initialize web3. Please refresh the page and try again.");
        } finally {
            setLoading(false);
        }
    }, []);

    // Initialize contracts
    const initializeContracts = async (web3Instance, networkId) => {
        try {
            // Check if the network is supported
            if (!CONTRACT_ADDRESSES[networkId]) {
                console.error(`Network ID ${networkId} not supported`);
                setError(`Network not supported. Please switch to Sepolia testnet.`);
                return;
            }

            const addresses = CONTRACT_ADDRESSES[networkId];

            console.log("Initializing contracts with addresses:", addresses);

            try {
                // Initialize contracts with proper error handling
                const marginTrading = new web3Instance.eth.Contract(
                    MarginTradingABI.abi, // Access the ABI property
                    addresses.marginTrading
                );

                const mockToken = new web3Instance.eth.Contract(
                    MockTokenABI.abi, // Access the ABI property
                    addresses.mockToken
                );

                const liquidationEngine = new web3Instance.eth.Contract(
                    LiquidationEngineABI.abi, // Access the ABI property
                    addresses.liquidationEngine
                );

                console.log("Contracts initialized successfully");

                setContracts({
                    marginTrading,
                    mockToken,
                    liquidationEngine
                });

                // Fetch initial ETH price
                await fetchEthPrice();
            } catch (contractError) {
                console.error("Error creating contract instances:", contractError);
                setError("Failed to initialize contract instances. Please check the contract ABIs and addresses.");
                return;
            }

        } catch (error) {
            console.error("Error initializing contracts:", error);
            setError("Failed to initialize contracts. Please refresh the page and try again.");
        }
    };

    // Connect wallet function for button
    const connectWallet = async () => {
        setLoading(true);
        setError(null);
        await initializeWeb3();
    };

    // Fetch ETH price (simulated for now)
    const fetchEthPrice = async () => {
        try {
            // In a real application, you would fetch this from an oracle or API
            // For now, we'll simulate a price with some random variation
            const basePrice = 1500;
            const randomVariation = Math.random() * 200 - 100; // -100 to +100
            const simulatedPrice = (basePrice + randomVariation).toFixed(2);
            setEthPrice(simulatedPrice);
        } catch (error) {
            console.error("Error fetching ETH price", error);
        }
    };

    // Refresh data function
    const refreshData = useCallback(async () => {
        if (web3 && account) {
            await fetchEthPrice();
        }
    }, [web3, account]);

    // Initialize web3 and contracts when the component mounts
    useEffect(() => {
        if (window.ethereum) {
            window.ethereum.on('accountsChanged', (accounts) => {
                if (accounts.length > 0) {
                    setAccount(accounts[0]);
                } else {
                    setAccount(null);
                }
            });

            window.ethereum.on('chainChanged', () => {
                window.location.reload();
            });
        }

        initializeWeb3();
    }, [initializeWeb3]);

    // Set up interval to refresh ETH price
    useEffect(() => {
        const interval = setInterval(() => {
            if (web3 && account) {
                fetchEthPrice();
            }
        }, 30000); // Every 30 seconds

        return () => clearInterval(interval);
    }, [web3, account]);

    // Context value
    const contextValue = {
        web3,
        account,
        networkId,
        contracts,
        loading,
        error,
        ethPrice,
        connectWallet,
        refreshData
    };

    return (
        <Web3Context.Provider value={contextValue}>
            {children}
        </Web3Context.Provider>
    );
};

// Custom hook to use the web3 context
export const useWeb3 = () => {
    const context = useContext(Web3Context);
    if (!context) {
        throw new Error('useWeb3 must be used within a Web3Provider');
    }
    return context;
};
