import React, { useState } from 'react';
import { Web3Provider, useWeb3 } from './context/Web3Context';
import TradingForm from './components/TradingForm';
import PositionInfo from './components/PositionInfo';
import './App.css';

// ConnectWallet component to handle wallet connection
const ConnectWallet = () => {
    const { connectWallet, account, error } = useWeb3();

    return (
        <div className="connect-wallet">
            {!account ? (
                <>
                    <button onClick={connectWallet} className="connect-button">
                        Connect MetaMask
                    </button>
                    {error && <p className="error-message">{error}</p>}
                </>
            ) : (
                <div className="account-info">
                    <span>Connected: {account.slice(0, 6)}...{account.slice(-4)}</span>
                </div>
            )}
        </div>
    );
};

// Main App content that requires wallet connection
const AppContent = () => {
    const { account, loading } = useWeb3();

    if (loading) {
        return <div className="loading">Loading web3 connection...</div>;
    }

    if (!account) {
        return (
            <div className="connect-container">
                <h2>Please connect your wallet to use the trading platform</h2>
                <ConnectWallet />
            </div>
        );
    }

    return (
        <div className="trading-container">
            <TradingForm />
            <PositionInfo />
        </div>
    );
};

function App() {
    return (
        <Web3Provider>
            <div className="App">
                <header className="App-header">
                    <h1>Margin Trading DApp</h1>
                    <ConnectWallet />
                </header>
                <main className="App-main">
                    <AppContent />
                </main>
            </div>
        </Web3Provider>
    );
}

export default App;
